"use strict";(self.webpackChunkmy_website=self.webpackChunkmy_website||[]).push([["63196"],{23784(e,t,n){n.r(t),n.d(t,{metadata:()=>o,default:()=>d,frontMatter:()=>a,contentTitle:()=>i,toc:()=>p,assets:()=>c});var o=JSON.parse('{"id":"company/Processes/Hiring/Response templates/UX designer","title":"UX designer","description":"In russian","source":"@site/about/company/Processes/Hiring/Response templates/UX designer.md","sourceDirName":"company/Processes/Hiring/Response templates","slug":"/company/Processes/Hiring/Response templates/UX designer","permalink":"/about/company/Processes/Hiring/Response templates/UX designer","draft":false,"unlisted":false,"tags":[],"version":"current","frontMatter":{},"sidebar":"tutorialSidebar","previous":{"title":"Robotics engineer","permalink":"/about/company/Processes/Hiring/Response templates/Robotics engineer"},"next":{"title":"Internship_IoT_Mechatronics","permalink":"/about/company/Processes/Hiring/Internship/Internship_IoT_Mechatronics"}}'),s=n(86070),r=n(18439);let a={},i,c={},p=[{value:"In russian",id:"in-russian",level:2}];function u(e){let t={code:"code",h2:"h2",pre:"pre",...(0,r.R)(),...e.components};return(0,s.jsxs)(s.Fragment,{children:[(0,s.jsx)(t.pre,{children:(0,s.jsx)(t.code,{children:"Thanks for applying to the Gratheon UX engineer volunteer position. I assume you aim to help beekeepers have an easier life in managing bees using intuitive software and  well designed robotic hives. \n\nJust wanted to reiterate that this is not a paid position. We're just getting the project started and our team is still very small (4) and we are not profitable. We\u2019ve had various hackathons and accelerators so far. The last one was prototron.ee where we got in the top 25 out of 50 teams.\n\nThe current status of the project is that we have a web-app that is open for anyone to register and try out at https://app.gratheon.com/. Our code and internal docs are fully open source and you\u2019re encouraged to check them out at https://github.com/Gratheon\n\nAs there are multiple candidates, and I'm not sure how best to evaluate you, so if you have time for a side project I suggest you to:\n\n1. Explore what you think is best you can do technically and what interests you the most out of our list of features we have in the backlog:\nhttps://gratheon.notion.site/Ideas-and-Features-ca76f493a1454e14b7dfbae7bd292a2e\n\n2. Join Discord to discuss a specific feature you could work on, but which is not clear / not groomed yet: https://discord.gg/PcbP4uedWj\n\n3. Question, analyze and improve features with beekeeper as user in mind. Design wireframes, user flows and UI components to be used in the web-app. (we use penpot.app for now)\n\n4. Optionally, you can check out our past meeting recordings - https://gratheon.notion.site/All-hands-meeting-recordings-405d4ff10fc04cd4a57dc8ed9ef362a9?pvs=74\n\nFeel free to explore Notion and I hope to hear back from you in the Discord.\n"})}),"\n",(0,s.jsx)(t.h2,{id:"in-russian",children:"In russian"}),"\n",(0,s.jsx)(t.pre,{children:(0,s.jsx)(t.code,{children:"\u0421\u043F\u0430\u0441\u0438\u0431\u043E \u0437\u0430 \u0437\u0430\u044F\u0432\u043A\u0443 \u043D\u0430 \u0432\u043E\u043B\u043E\u043D\u0442\u0451\u0440\u0441\u043A\u0443\u044E \u043F\u043E\u0437\u0438\u0446\u0438\u044E UX-\u0438\u043D\u0436\u0435\u043D\u0435\u0440\u0430 \u0432 Gratheon. \u041F\u043E\u043B\u0430\u0433\u0430\u044E, \u0442\u044B \u0445\u043E\u0447\u0435\u0448\u044C \u043F\u043E\u043C\u043E\u0447\u044C \u043F\u0447\u0435\u043B\u043E\u0432\u043E\u0434\u0430\u043C \u0443\u043F\u0440\u043E\u0441\u0442\u0438\u0442\u044C \u0436\u0438\u0437\u043D\u044C \u0432 \u0443\u043F\u0440\u0430\u0432\u043B\u0435\u043D\u0438\u0438 \u043F\u0447\u0451\u043B\u0430\u043C\u0438 \u0441 \u043F\u043E\u043C\u043E\u0449\u044C\u044E \u0438\u043D\u0442\u0443\u0438\u0442\u0438\u0432\u043D\u043E\u0433\u043E \u041F\u041E \u0438 \u0445\u043E\u0440\u043E\u0448\u043E \u0440\u0430\u0437\u0440\u0430\u0431\u043E\u0442\u0430\u043D\u043D\u044B\u0445 \u0440\u043E\u0431\u043E-\u0443\u043B\u044C\u0435\u0432.\n\n\u041F\u0440\u043E\u0441\u0442\u043E \u0445\u043E\u0447\u0443 \u0435\u0449\u0451 \u0440\u0430\u0437 \u043D\u0430\u043F\u043E\u043C\u043D\u0438\u0442\u044C, \u0447\u0442\u043E \u044D\u0442\u043E \u043D\u0435 \u043E\u043F\u043B\u0430\u0447\u0438\u0432\u0430\u0435\u043C\u0430\u044F \u043F\u043E\u0437\u0438\u0446\u0438\u044F. \u041C\u044B \u0442\u043E\u043B\u044C\u043A\u043E \u043D\u0430\u0447\u0438\u043D\u0430\u0435\u043C \u043F\u0440\u043E\u0435\u043A\u0442, \u0438 \u043D\u0430\u0448\u0430 \u043A\u043E\u043C\u0430\u043D\u0434\u0430 \u043F\u043E\u043A\u0430 \u043E\u0447\u0435\u043D\u044C \u043C\u0430\u043B\u0435\u043D\u044C\u043A\u0430\u044F (4) \u0438 \u043C\u044B \u043D\u0435 \u043F\u0440\u0438\u0431\u044B\u043B\u044C\u043D\u044B. \u0423 \u043D\u0430\u0441 \u0431\u044B\u043B\u043E \u043D\u0435\u0441\u043A\u043E\u043B\u044C\u043A\u043E \u0445\u0430\u043A\u0430\u0442\u043E\u043D\u043E\u0432 \u0438 \u0430\u043A\u0441\u0435\u043B\u0435\u0440\u0430\u0442\u043E\u0440\u043E\u0432. \u041F\u043E\u0441\u043B\u0435\u0434\u043D\u0438\u0439 \u0431\u044B\u043B \u043D\u0430 prototron.ee, \u0433\u0434\u0435 \u043C\u044B \u0432\u043E\u0448\u043B\u0438 \u0432 \u0442\u043E\u043F 25 \u0438\u0437 50 \u043A\u043E\u043C\u0430\u043D\u0434.\n\n\u041D\u0430 \u0434\u0430\u043D\u043D\u044B\u0439 \u043C\u043E\u043C\u0435\u043D\u0442 \u0443 \u043D\u0430\u0441 \u0435\u0441\u0442\u044C \u0432\u0435\u0431-\u043F\u0440\u0438\u043B\u043E\u0436\u0435\u043D\u0438\u0435, \u0434\u043E\u0441\u0442\u0443\u043F\u043D\u043E\u0435 \u0434\u043B\u044F \u0440\u0435\u0433\u0438\u0441\u0442\u0440\u0430\u0446\u0438\u0438 \u0438 \u0442\u0435\u0441\u0442\u0438\u0440\u043E\u0432\u0430\u043D\u0438\u044F \u0432\u0441\u0435\u043C \u0436\u0435\u043B\u0430\u044E\u0449\u0438\u043C: https://app.gratheon.com/. \u041D\u0430\u0448 \u043A\u043E\u0434 \u0438 \u0432\u043D\u0443\u0442\u0440\u0435\u043D\u043D\u0438\u0435 \u0434\u043E\u043A\u0443\u043C\u0435\u043D\u0442\u044B \u043F\u043E\u043B\u043D\u043E\u0441\u0442\u044C\u044E \u043E\u0442\u043A\u0440\u044B\u0442\u044B, \u0438 \u0442\u044B \u043C\u043E\u0436\u0435\u0448\u044C \u043E\u0437\u043D\u0430\u043A\u043E\u043C\u0438\u0442\u044C\u0441\u044F \u0441 \u043D\u0438\u043C\u0438 \u0437\u0434\u0435\u0441\u044C: https://github.com/Gratheon\n\n\u0422\u0430\u043A \u043A\u0430\u043A \u0435\u0441\u0442\u044C \u043D\u0435\u0441\u043A\u043E\u043B\u044C\u043A\u043E \u043A\u0430\u043D\u0434\u0438\u0434\u0430\u0442\u043E\u0432, \u0438 \u044F \u043D\u0435 \u0443\u0432\u0435\u0440\u0435\u043D, \u043A\u0430\u043A \u043B\u0443\u0447\u0448\u0435 \u0442\u0435\u0431\u044F \u043E\u0446\u0435\u043D\u0438\u0442\u044C, \u0435\u0441\u043B\u0438 \u0443 \u0442\u0435\u0431\u044F \u0435\u0441\u0442\u044C \u0432\u0440\u0435\u043C\u044F \u0434\u043B\u044F \u0441\u0430\u0439\u0434-\u043F\u0440\u043E\u0435\u043A\u0442\u0430, \u044F \u043F\u0440\u0435\u0434\u043B\u0430\u0433\u0430\u044E:\n\n\u0418\u0437\u0443\u0447\u0438\u0442\u044C, \u0447\u0442\u043E, \u043F\u043E \u0442\u0432\u043E\u0435\u043C\u0443 \u043C\u043D\u0435\u043D\u0438\u044E, \u0442\u044B \u043C\u043E\u0436\u0435\u0448\u044C \u0441\u0434\u0435\u043B\u0430\u0442\u044C \u0442\u0435\u0445\u043D\u0438\u0447\u0435\u0441\u043A\u0438 \u0438 \u0447\u0442\u043E \u0442\u0435\u0431\u044F \u0431\u043E\u043B\u044C\u0448\u0435 \u0432\u0441\u0435\u0433\u043E \u0438\u043D\u0442\u0435\u0440\u0435\u0441\u0443\u0435\u0442 \u0438\u0437 \u043D\u0430\u0448\u0435\u0433\u043E \u0441\u043F\u0438\u0441\u043A\u0430 \u0444\u0438\u0447 \u0432 \u0431\u044D\u043A\u043B\u043E\u0433\u0435: https://gratheon.notion.site/Ideas-and-Features-ca76f493a1454e14b7dfbae7bd292a2e.\n\n\u041F\u0440\u0438\u0441\u043E\u0435\u0434\u0438\u043D\u0438\u0442\u044C\u0441\u044F \u043A Discord, \u0447\u0442\u043E\u0431\u044B \u043E\u0431\u0441\u0443\u0434\u0438\u0442\u044C \u043A\u043E\u043D\u043A\u0440\u0435\u0442\u043D\u0443\u044E \u0444\u0438\u0447\u0443, \u043D\u0430\u0434 \u043A\u043E\u0442\u043E\u0440\u043E\u0439 \u0442\u044B \u043C\u043E\u0433 \u0431\u044B \u0440\u0430\u0431\u043E\u0442\u0430\u0442\u044C, \u043D\u043E \u043A\u043E\u0442\u043E\u0440\u0430\u044F \u043F\u043E\u043A\u0430 \u043D\u0435 \u044F\u0441\u043D\u0430 / \u043D\u0435 \u043F\u0440\u043E\u0440\u0430\u0431\u043E\u0442\u0430\u043D\u0430: https://discord.gg/PcbP4uedWj.\n\n\u0421\u043F\u0440\u0430\u0448\u0438\u0432\u0430\u0439, \u0430\u043D\u0430\u043B\u0438\u0437\u0438\u0440\u0443\u0439 \u0438 \u0443\u043B\u0443\u0447\u0448\u0438 \u0444\u0443\u043D\u043A\u0446\u0438\u0438 \u0441 \u0443\u0447\u0451\u0442\u043E\u043C \u043F\u0447\u0435\u043B\u043E\u0432\u043E\u0434\u0430 \u043A\u0430\u043A \u043F\u043E\u043B\u044C\u0437\u043E\u0432\u0430\u0442\u0435\u043B\u044F. \u041C\u043E\u0436\u043D\u043E \u043D\u0430\u0447\u0430\u0442\u044C \u0441 \u0440\u0430\u0437\u0440\u0430\u0431\u043E\u0442\u043A\u0438 \u0432\u0430\u0439\u0440\u0444\u0440\u0435\u0439\u043C\u043E\u0432, \u043F\u043E\u043B\u044C\u0437\u043E\u0432\u0430\u0442\u0435\u043B\u044C\u0441\u043A\u043E\u0433\u043E \u043F\u0443\u0442\u0438 \u0438 UI-\u043A\u043E\u043C\u043F\u043E\u043D\u0435\u043D\u0442\u043E\u0432 \u0434\u043B\u044F \u0438\u0441\u043F\u043E\u043B\u044C\u0437\u043E\u0432\u0430\u043D\u0438\u044F \u0432 \u0432\u0435\u0431-\u043F\u0440\u0438\u043B\u043E\u0436\u0435\u043D\u0438\u0438, \u043D\u0430\u043F\u0440\u0438\u043C\u0435\u0440 \u043C\u043E\u0436\u043D\u043E \u0438\u0441\u043F\u043E\u043B\u044C\u0437\u043E\u0432\u0430\u0442\u044C \u0431\u0435\u0441\u043F\u043B\u0430\u0442\u043D\u044B\u0439 penpot.app - \u044D\u0442\u043E \u043F\u043E\u043C\u043E\u0436\u0435\u0442 \u043E\u0441\u0442\u0430\u043B\u044C\u043D\u044B\u043C \u0440\u0430\u0437\u0440\u0430\u0431\u043E\u0442\u0447\u0438\u043A\u0430\u043C \u043F\u043E\u043D\u044F\u0442\u044C \u043A\u0430\u043A \u043A\u0430\u043A\u0430\u044F \u0444\u0438\u0447\u0430 \u0434\u043E\u043B\u0436\u043D\u0430 \u0440\u0430\u0431\u043E\u0442\u0430\u0442\u044C\n\n\u0412\u043E\u0437\u043C\u043E\u0436\u043D\u043E, \u044D\u0442\u043E \u0432\u0438\u0434\u0435\u043E \u043F\u043E\u043C\u043E\u0436\u0435\u0442 \u0441 \u043F\u0435\u0440\u0432\u043E\u043D\u0430\u0447\u0430\u043B\u044C\u043D\u044B\u043C \u0437\u043D\u0430\u043A\u043E\u043C\u0441\u0442\u0432\u043E\u043C - https://www.youtube.com/watch?v=4awyyk8sWho.\n\n\u041F\u043E\u0438\u0441\u0441\u043B\u0435\u0434\u0443\u0439 Notion \u0447\u0442\u043E-\u0431\u044B \u043E\u0437\u043D\u0430\u043A\u043E\u043C\u0438\u0442\u044C\u0441\u044F \u043F\u043E\u0434\u0440\u043E\u0431\u043D\u0435\u0439, \u0438 \u043D\u0430\u0434\u0435\u044E\u0441\u044C \u0443\u0441\u043B\u044B\u0448\u0430\u0442\u044C \u043E\u0442 \u0442\u0435\u0431\u044F \u0432 Discord.\n"})})]})}function d(e={}){let{wrapper:t}={...(0,r.R)(),...e.components};return t?(0,s.jsx)(t,{...e,children:(0,s.jsx)(u,{...e})}):u(e)}},18439(e,t,n){n.d(t,{R:()=>a,x:()=>i});var o=n(30758);let s={},r=o.createContext(s);function a(e){let t=o.useContext(r);return o.useMemo(function(){return"function"==typeof e?e(t):{...t,...e}},[t,e])}function i(e){let t;return t=e.disableParentContext?"function"==typeof e.components?e.components(s):e.components||s:a(e.components),o.createElement(r.Provider,{value:t},e.children)}}}]);